import React, {Component} from 'react';
import {useState} from 'react';

require('dotenv').config()



function loginUser(){
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

}

class Login extends Component{
    constructor(props){
        super(props)
        this.state = {
                username: "",
                password: ""
              };
        }



    render(){

        // console.log(process.env.REACT_APP_API_URL);
        return(
            <div className="hold-transition login-page">

                <div className="login-box">
                    <div className="login-logo">
                        <a href="../../index2.html"><b>e</b> - Komunitas</a>
                    </div>
                    {/* /.login-logo */}
                    <div className="card">
                        <div className="card-body login-card-body">
                        <p className="login-box-msg">Sign in to start your session</p>
                        <form action={process.env.REACT_APP_API_URL+"/login"} method="post">
                            <div className="input-group mb-3">
                            <input type="email" value={username} onChange={(e)=>setUsername(e.target.value)} className="form-control" placeholder="Email" />
                            <div className="input-group-append">
                                <div className="input-group-text">
                                <span className="fas fa-envelope" />
                                </div>
                            </div>
                            </div>
                            <div className="input-group mb-3">
                            <input type="password" value={password} onChange={(e)=>setPassword(e.target.value)} className="form-control" placeholder="Password" />
                            <div className="input-group-append">
                                <div className="input-group-text">
                                <span className="fas fa-lock" />
                                </div>
                            </div>
                            </div>
                            <div className="row">
                            <div className="col-8">
                                <div className="icheck-primary">
                                <input type="checkbox" id="remember" />
                                <label htmlFor="remember">
                                    Remember Me
                                </label>
                                </div>
                            </div>
                            {/* /.col */}
                            <div className="col-4">
                                <button type="submit" className="btn btn-primary btn-block">Sign In</button>
                            </div>
                            {/* /.col */}
                            </div>
                        </form>
                        {/* <div className="social-auth-links text-center mb-3">
                            <p>- OR -</p>
                            <a href="#" className="btn btn-block btn-primary">
                            <i className="fab fa-facebook mr-2" /> Sign in using Facebook
                            </a>
                            <a href="#" className="btn btn-block btn-danger">
                            <i className="fab fa-google-plus mr-2" /> Sign in using Google+
                            </a>
                        </div> */}
                        {/* /.social-auth-links */}
                        <p className="mb-1">
                            <a href="forgot-password.html">I forgot my password</a>
                        </p>
                        <p className="mb-0">
                            <a href="register.html" className="text-center">Register a new membership</a>
                        </p>
                        </div>
                        {/* /.login-card-body */}
                    </div>
                </div>
            </div>
        );
    }
}

export default Login;
