import React, {Component, Document} from 'react';
import DataTable, { createTheme } from 'react-data-table-component';
createTheme('solarized', {
    text: {
      primary: '#268bd2',
      secondary: '#2aa198',
    },
    background: {
      default: '#002b36',
    },
    context: {
      background: '#cb4b16',
      text: '#FFFFFF',
    },
    divider: {
      default: '#073642',
    },
    action: {
      button: 'rgba(0,0,0,.54)',
      hover: 'rgba(0,0,0,.08)',
      disabled: 'rgba(0,0,0,.12)',
    },
  });


  class Datatable extends Component{
      render(){
        const columns = [
            {
                name: 'No',
                sortable: true,
                cell: row => <div data-tag="allowRowEvents"><div>{row.title}</div>{row.summary}</div>,
            },
            {
                name: 'Title',
                sortable: true,
                cell: row => <div data-tag="allowRowEvents"><div>{row.title}</div>{row.summary}</div>,
            },
        ];

        const MyDatatable = () => (
            <DataTable
                title="Tabel Warga"
                columns={columns}
                theme="solarized"
            />
        );
        return(<MyDatatable />);
    }
}

export default Datatable;
