import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { If, Then, ElseIf, Else } from 'react-if-elseif-else-render';


class GenerateMenus extends Component{
    render(){
        const menuList = [
            {
                'icon'  : 'tachometer-alt',
                'perms' : '#',
                'path'  : 'javascript();',
                'name'  : 'Dashboard',
                'sub'   : [
                    {
                        'icon'  : 'circle',
                        'perms' : '#',
                        'path'  : 'pengunjung',
                        'name'  : 'Pengunjung',
                        'sub'   : []
                    },
                    {
                        'icon'  : 'circle',
                        'perms' : '#',
                        'path'  : 'pengguna',
                        'name'  : 'Pengguna',
                        'sub'   : []
                    },
                    {
                        'icon'  : 'circle',
                        'perms' : '#',
                        'path'  : 'komunitas',
                        'name'  : 'Komunitas',
                        'sub'   : []
                    },
                ]
            },
            {
                'icon'  : 'list',
                'perms' : '#',
                'path'  : '/komunitas',
                'name'  : 'Kominitas Terdaftar',
                'sub'   : []
            },
            {
                'icon'  : 'users',
                'perms' : '#',
                'path'  : 'javascript();',
                'name'  : 'Data Warga',
                'sub'   : []
            },
            {
                'icon'  : 'file',
                'perms' : 'javascript();',
                'path'  : '/master',
                'name'  : 'Master',
                'sub'   : [
                    {
                        'icon'  : 'circle',
                        'perms' : '#',
                        'path'  : '/inventaris',
                        'name'  : 'Inventaris',
                        'sub'   : []
                    },
                    {
                        'icon'  : 'circle',
                        'perms' : '#',
                        'path'  : '/jenis-dokumen',
                        'name'  : 'Jenis Dokumen',
                        'sub'   : []
                    },
                    {
                        'icon'  : 'circle',
                        'perms' : '#',
                        'path'  : '/kategori-iuran',
                        'name'  : 'Kategori Iuran',
                        'sub'   : []
                    },
                    {
                        'icon'  : 'circle',
                        'perms' : '#',
                        'path'  : '/kategori-pengaduan',
                        'name'  : 'Kategori Pengaduan',
                        'sub'   : []
                    },
                    {
                        'icon'  : 'circle',
                        'perms' : '#',
                        'path'  : '/kategori-pengaduan',
                        'name'  : 'Kategori Pengaduan',
                        'sub'   : []
                    }
                ]
            },
            {
                'icon'  : 'wrench',
                'perms' : '#',
                'path'  : '/setting',
                'name'  : 'Settings',
                'sub'   : [
                    {
                        'icon'  : 'circle',
                        'perms' : '#',
                        'path'  : '/setting',
                        'name'  : 'Role Akses',
                        'sub'   : []
                    }
                ]
            }
        ];
        const MenuRoute = menuList.map((item, key)=>{
            console.log(key);
            // console.log(item.sub.length)
            var open = '';
            if(key == 0){
                open = "menu-open";
            }
            return(
                <Router>
                    <li className={"nav-item "+(item.sub.length!==0?("has-treeview "+open):"")+" "}>
                        <a href={item.path} className="nav-link">
                            <i className={"nav-icon fas fa-"+item.icon} />
                            <p>
                            {item.name}
                            {item.sub.length!==0?(<i className='right fas fa-angle-left' />):""}
                            </p>
                        </a>
                        <If condition={item.sub.length>0}>
                            <Then>
                                <ul className="nav nav-treeview">
                                        {item.sub.map((items)=>{
                                            return(
                                                <li className="nav-item">
                                                    <a href={items.path} className="nav-link ">
                                                        <i className={"far nav-icon fa-"+items.icon} />
                                                        <p>{items.name}</p>
                                                    </a>
                                                </li>
                                            );
                                        })}
                                </ul>
                            </Then>

                        </If>
                        {/* {item.sub.map((itms)=>{

                            return();
                        })} */}
                    </li>
                </Router>
            );
        });

        return(
            <ul className="nav nav-pills nav-sidebar flex-column" data-widget="treeview" role="menu" data-accordion="false">
                {MenuRoute}
            </ul>
        );
    }
}

export default GenerateMenus;
